import styles from "./Login.module.css";
import React, { useState } from "react";
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
} from "firebase/auth";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // login auth
  const [isLogin, setIsLgoin] = useState(true);
  const [error, setError] = useState(null);

  const auth = getAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (isLogin) {
      try {
        const userCredential = await signInWithEmailAndPassword(
          auth,
          email,
          password
        );
        console.log("User signed in:", userCredential.user);
      } catch (err) {
        console.error("Error during sign in:", err.code, err.message);
        setError(err.message);
      }
    } else {
      try {
        const userCredential = await createUserWithEmailAndPassword(
          auth,
          email,
          password
        );
        console.log("User signed up:", userCredential.user);
      } catch (err) {
        console.error("Error during sign up:", err.code, err.message);
        setError(err.message);
      }
    }
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>{isLogin ? "Log In" : "Sign Up"}</h1>
      <form onSubmit={handleSubmit} className={styles.form}>
        <input
          type="text"
          placeholder="Enter Your Email"
          className={styles.input}
          onChange={(e) => setEmail(e.target.value)}
          value={email}
        />
        <input
          type="password"
          placeholder="Enter Your Password"
          className={styles.input}
          onChange={(p) => setPassword(p.target.value)}
          value={password}
        />
        <button type="submit" className={styles.button} disabled={isPending}>
          {isPending ? "Loading..." : isLogin ? "Log In" : "Sign Up"}
        </button>
        {error && <p className={styles.error}>{error}</p>}
      </form>
      <p className={styles.toggle} onClick={() => setIsLogin(!isLogin)}>
        {isLogin
          ? "Don't have an account? Sign Up"
          : "Already have an account? Log In"}
      </p>
    </div>
  );
}
