// this is custom hook to manage fetching on review data according to product #
import { useState, useEffect } from "react";
import { projectFirestore, projectAuth } from "../firebase/config";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { onAuthStateChanged } from "firebase/auth";
//adding time stamp
import { Timestamp } from "firebase/firestore";

const useReviews = (productNumber) => {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchReviews = async () => {
      const getReviews = await getDocs(
        query(
          collection(projectFirestore, "reviews"),
          where("product", "==", productNumber),
          orderBy("timestamp", "desc")
        )
      );
      const reviewsData = getReviews.docs.map((doc) => doc.data());
      setReviews(reviewsData);
    };

    fetchReviews();
  }, [productNumber]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(projectAuth, (user) => {
      if (user) {
        setUser(user);
      } else {
        setUser(null);
      }
    });

    return () => unsubscribe();
  }, []);

  const handleReviewSubmit = async () => {
    const newReview = {
      username: user ? user.email : "Anonymous",
      date: new Date().toLocaleDateString(),
      text: reviewText,
      product: productNumber,
      timestamp: Timestamp.now(),
    };

    try {
      await addDoc(collection(projectFirestore, "reviews"), newReview);
      setReviews([...reviews, newReview]);
      setReviewText("");
    } catch (err) {
      console.error("Error adding a new review: ", err);
    }
  };

  return {
    reviews,
    reviewText,
    setReviewText,
    handleReviewSubmit,
  };
};

export default useReviews;
